/**
 * 位置类
 *
 */
public class Location {
	
	private int x = -1;
	private int y = -1;
	
	public Location(int x, int y) {
		this.x = x;
		this.y = y;
	}
	
	public Location() {
		// TODO Auto-generated constructor stub
	}
	
	public int getX() {
		return x;
	}

	public void setX(int x) {
		this.x = x;
	}

	public int getY() {
		return y;
	}

	public void setY(int y) {
		this.y = y;
	}
	
}